frappe.listview_settings['Therapy Session'] = {
    onload: function(listview) {
        listview.page.add_inner_button(__('Merge Invoice'), function() {
            const selected_docs = listview.get_checked_items();

            if (selected_docs.length === 0) {
                frappe.msgprint(__('Please select at least one Therapy Session'));
                return;
            }

            const therapy_sessions = selected_docs.map(doc => doc.name);
            console.log("::::::::::::therapy_sessions:::",therapy_sessions)

            frappe.call({
                method: "swissmedhealth.swissmedhealth.hooks.custom_therapy_session.merge_therapy_sessions_to_invoice",
                args: {
                    'therapy_sessions': therapy_sessions
                },
                callback: function(r) {
                    if(r.message) {
                        console.log("::::::::::::::::::::",r.message)
                        frappe.msgprint(__('Invoice Created: <a href = "/app/sales-invoice/'+r.message+'">'+r.message+'</a>'));
                        listview.refresh();
                    }
                }
            });
        });
    }
};

frappe.ui.form.on('Therapy Plan', {
	refresh: function(frm,cdt,cdn) {
	    frm.page.remove_inner_button(__('Therapy Session'),  __('Create'));
		frm.add_custom_button(__("Treatment Appointment"), function() {	    		
			frappe.new_doc("Therapy Session", {
			    "therapy_plan": frm.doc.name,
				"patient": frm.doc.patient,
			});
		}, __("Create"));
	}
});

