script:
frappe.ui.form.on('Therapy Plan', {
	refresh: function(frm,cdt,cdn) {
	    frm.page.remove_inner_button(__('Therapy Session'),  __('Create'));
		frm.add_custom_button(__("Treatment Appointment"), function() {	    		
			frappe.new_doc("Therapy Session", {
			    "therapy_plan": frm.doc.name,
				"patient": frm.doc.patient,
			});
		}, __("Create"));
	}
});

frappe.views.calendar["Therapy Session"] = {
	field_map: {
		"status": "Draft",
        "id": "name",
        "title": "patient",
        "start": "start",
        "end": "end",  // Make sure you have an end_date if needed
        // "allDay": "all_day", // Optional, if you are using all-day events
        "therapy_plan": "therapy_plan",
        "eventColor": "color"
	},
	order_by: "start_date",
	gantt: true,
	get_events_method: "swissmedhealth.public.therapy_session.get_events"
};
