css:
.sales-invoice-custom {
    border: 2px solid;
    padding: 2px;
}

.section-break {
    border-bottom: 1px solid;
    margin: 1px;
}

.sales-invoice-table {
    width: 100%;
    border: 1px solid;
}
.sales-invoice-table td {
    border-left: 1px solid;
    border-right: 1px solid;
}
.sales-invoice-table th {
    border: 1px solid;
}

.border-bottom {
    border-bottom: 1px solid;
}
